import styled from "styled-components"
import tw from "twin.macro"

const StyleForm = styled.div.attrs({
  className: "",
})`
  & {
    label {
      ${tw`text-sm font-bold text-gray-700 tracking-wide`}
    }
    input {
      ${tw`w-full text-lg py-2 border-b border-gray-300 focus:outline-none focus:border-indigo-500`}
    }
    button {
      ${tw`bg-indigo-500 text-gray-100 p-4 w-full rounded-full tracking-wide
      font-semibold focus:outline-none hover:bg-indigo-600 disabled:opacity-50
      shadow-lg`}
    }
  }
`
export default StyleForm
