// import styled from "styled-components"
// import tw from "twin.macro"
import styled from "styled-components"
// import tw from "twin.macro"

const StyledForm = styled.div.attrs({
  className: "flex flex-col justify-center items-center bg-gray-100",
})`
  & {
    form {
      ${tw`bg-white text-center rounded flex`}
    }
    input {
      ${tw`border-gray-300 border-solid border rounded py-2 px-4`}
    }
    select {
      ${tw`lock appearance-none w-full bg-gray-200 border border-gray-200 text-gray-700 py-3 px-4 pr-8 rounded leading-tight focus:outline-none focus:bg-white focus:border-gray-500`}
    }
    button {
      ${tw`bg-green-500 hover:bg-green-700 text-white font-bold py-2 px-4 border border-blue-700 rounded`}
    }
  }
`
export default StyledForm;
