import React, { useEffect } from 'react';
import { useSelector } from 'react-redux';
import fetchApi from '../../services/fetchApi';

export default function Recomendations() {
  const { detail } = useSelector((state) => state);
  const [isFetching, setIsFetching] = React.useState(false);
  const [recomendations, setRecomendations] = React.useState([]);
  let key = Object.keys(detail)[0];
  const recipe = detail[key][0];
  const { strYoutube } = recipe;
  const idYoutube = strYoutube ? strYoutube.split('v=')[1] : null;
  let path = window.location.pathname.split('/')[1];
  let strTitle = null;
  let strThumb = null;
  if (path === 'comidas') {
    strTitle = 'strDrink';
    strThumb = 'strDrinkThumb';
    path = '/bebidas';
    key = 'drinks';
  }
  if (path === 'bebidas') {
    strTitle = 'strMeal';
    strThumb = 'strMealThumb';
    path = '/comidas';
    key = 'meals';
  }

  useEffect(() => {
    const fetchData = async () => {
      const response = await fetchApi({
        inputRadio: 'name',
        search: '',
      }, path);
      setRecomendations(response[key]);
      setIsFetching(true);
    };
    fetchData();
  }, [key, path]);
  if (!isFetching) return <div>Loading...</div>;
  return (
    <div>
      { strYoutube && (
        <div className="mb-7 flex flex-col">
          <h1 className="font-bold text-lg px-4">VÃ­deo</h1>
          <iframe
            src={ `https://www.youtube.com/embed/${idYoutube}` }
            title="video"
            frameBorder="0"
            allowFullScreen
            data-testid="video"
            className="justify-self-center md:w-1/2"
          />
        </div>
      )}
      <div className="w-full">
      <p className="font-bold text-lg mb-3">Recomendations</p>
      <div  className="flex w-full overflow-x-scroll">
        { recomendations.map((item, index) => {
          const MAX_CARDS = 5;
          if (index > MAX_CARDS) return null;
          return (
            <div
              key={ index }
              className="w-80 sm:w-96 md:w-72 mr-3 border border-gray-500"
              data-testid={ `${index}-recomendation-card` }
            >
              <img  className="w-72 max-w-none" src={ item[strThumb] } alt={ item[strTitle] } />
              <p data-testid={ `${index}-recomendation-title` }>{item[strTitle]}</p>
            </div>
          );
        })}
      </div>
      </div>
    </div>
  );
}
