import PropTypes from 'prop-types';
import React, { useState } from 'react';
import { Link } from 'react-router-dom';
import profileIcon from '../images/profileIcon.svg';
import searchIcon from '../images/searchIcon.svg';
import HeaderSearch from './HeaderSearch';

export default function Header({ searchRender, title }) {
  const [clickSearch, setclickSearch] = useState(false);

  const searchButton = () => (
    <button
      type="button"
      onClick={ () => setclickSearch(!clickSearch) }
      className="bg-transparent border-0"
    >
      <img
        src={ searchIcon }
        data-testid="search-top-btn"
        alt="icone de busca"
      />
    </button>
  );

  return (
    <div className="p-2 bg-red-200	">
      <div className="flex justify-between">
        <div className="">
          <Link to="/perfil">
            <img
              src={ profileIcon }
              data-testid="profile-top-btn"
              alt="icone de perfil"
            />
          </Link>
        </div>
        <div className="mx-2">
          <h1 data-testid="page-title" className="text-4xl">{ title }</h1>
        </div>
        <div>
        { searchRender
        && searchButton() }
        </div>
      </div>
      <div className="ml-4">
      { clickSearch && <HeaderSearch />}
      </div>
    </div>
  );
}

Header.propTypes = {
  searchRender: PropTypes.bool,
  title: PropTypes.string.isRequired,
};

Header.defaultProps = {
  searchRender: true,
};
